import React, { Component } from 'react';
import employees from '../../employees.json';
import FightCard from '../FightCard';

class Search extends Component{
    constructor(props){
        super(props)
        this.state = {
            friends: employees,
            filteredEmployees: []
        }
    }
    filterList = e => {
        const filterTarget = e.target.value;
        let employeeList = this.state.friends;
        employeeList = employeeList.filter(i =>{
          let values = Object.values(i)
            .join("")
            .toLocaleLowerCase();
            return values.indexOf(filterTarget.toLowerCase()) !== -1;
        })
        this.setState({filteredEmployees: employeeList});
      }

      render(){
        return(
          <div className="filter-list">
            <input className="search" type="text" placeholder="Search" onChange={this.filterList}/>

            {
              this.state.filteredEmployees[0]?

              this.state.filteredEmployees.map(friends => (
                <FightCard
                id={friends.id}
                key={friends.id}
                name={friends.name}
                image={friends.image}
                position={friends.position}
                birth={friends.birth}
                country={friends.country}
                />
              )) 
              :
              this.state.friends.map(friends => (
                <FightCard
                id={friends.id}
                key={friends.id}
                name={friends.name}
                image={friends.image}
                position={friends.position}
                birth={friends.birth}
                country={friends.country}
                />
              ))


            }


          </div>
        )
      }
}


export default Search;
